setup:-
	g_assign(test1, 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'),
	g_assign(test2, 'bvwbjplbgvbhsrlpgdmjqwftvncz'),
	g_assign(main, '').


do_test(GV, N):-
	g_read(GV, Str),
	do_it(Str, N).

do_test_p2(GV, N):-
	g_read(GV, Str),
	do_it_p2(Str, N).

do_it(Str, N):-
	atom_chars(Str, Lst),
	find_id(Lst, N).
	
do_it_p2(Str, N):-
	atom_chars(Str, Lst),
	find_msg(Lst, N).
	
find_id([A,B,C,D|_], 4) :-
	no_duplicates([A,B,C,D]).

find_id([A,B,C,D|Tail], N) :-
	\+no_duplicates([A,B,C,D]),
	find_id([B,C,D|Tail], N1),
	N is N1 + 1.
	
find_msg([A,B,C,D,E,F,G,H,I,J,K,L,M,N|_], 14) :-
	no_duplicates([A,B,C,D,E,F,G,H,I,J,K,L,M,N]).
	
find_msg([A,B,C,D,E,F,G,H,I,J,K,L,M,N|Tail], Count) :-
	\+no_duplicates([A,B,C,D,E,F,G,H,I,J,K,L,M,N]),
	find_msg([B,C,D,E,F,G,H,I,J,K,L,M,N|Tail], Count1),
	Count is Count1 + 1.
	
no_duplicates(List) :-
	sort(List,Sorted),
	length(List, N),
	length(Sorted,N).